import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import {FiArrowLeft} from 'react-icons/fi';
import api from '../../services/api';
import './style.css';
import heroesLogo from '../../assets/logo.svg'
export default function Register(){
    const [nome,setNome]=useState('');
    const [email,setEmail]=useState('');
    const [whatsApp,setWhatsapp]=useState('');
    const [city,setCity]=useState('');
    const [uf,setUf]=useState('');
  async function handRegister(e){
    e.preventDefault();

    const data={
        nome,
        email,
        whatsApp,
        city,
        uf,
    };
   try{
    const response = await api.post('ongs',data);
    alert(`Seu ID de acesso: ${response.id}`);
   }catch{
       alert('Erro')
   }
   }
   
   
    return(
        <div className="register-container">
            <div className="content">
                <section>
                    <img src={heroesLogo} alt="Be The Hero"/>
                    <h1>Cadastro</h1>
                    <p>Faça seu cadastro, entre na plataform e ajude pessoas a encontrarem os casos da sua ONG</p>
                    <Link className="back-link" to="/">
                    <FiArrowLeft size={16}  color="#e02041" />
                    Não tenho Cadastro
                </Link>
                </section>
                <form onSubmit={handRegister}>
                    <input placeholder="Nome da ONG" 
                    value={nome}
                    onChange={e=> setNome(e.target.value)}
                    />
                    
                    <input type="email" placeholder="E-mail" 
                     value={email}
                     onChange={e=> setEmail(e.target.value)
                     }/>

                    <input placeholder="whatsApp"
                     value={whatsApp}
                     onChange={e=> setWhatsapp(e.target.value)
                     }/>

                    <div className="input-grup">
                        <input placeholder="cidade"
                         value={city}
                         onChange={e=> setCity(e.target.value)
                         } />

                        <input  placeholder="UF" style={{ width: 80 }}
                         value={uf}
                         onChange={e=> setUf(e.target.value)}
                        />
                    </div>
                    <button className="button" type="submit">Cadastrar</button>
                </form>
            </div>
        </div>
    );
}
