import React, {useEffect} from 'react';
import { Link } from 'react-router-dom';
import heroesLogo from '../../assets/logo.svg'
import {FiPower,FiTrash2} from 'react-icons/fi';
import api from '../../services/api';
import './styles.css'
export default function Profile(){
    const [incidents,setIncidents ] =useState([])
    const ongId  = localStorage.getItem('ongId');
    const onNome = localStorage.getItem('onNome');
    useEffect(()=> {
        api.get('profile',{
            headers:{
                Authorization:ongId,
            }
        }).then(Response =>{
            setIncidents(Response.data);
        })
    },[ongId ]);
    return (
        <div className="profile-container">
            <header>
                <img src={heroesLogo} alt="Be The Hero" />
                    <span>Bem vindo,{onNome}</span>

                <Link className="button" to="/incidents/new">Cadastart nov caso</Link>
                <button type="button">
                    <FiPower size={18} color="#E02041"/>
                </button>
            </header>
            <h1>casos cadastrados</h1>
            <ul>
               {incidents.map(incidents=>(
                    <li>
                    <strong>Caso:</strong>
                    <p >Caso teste</p>

                    <strong>Descrição:</strong>
                    <p>Decrição teste</p>
                    <strong>Valor</strong>
                    <p>R$120.00</p>

                    <button Type="button">
                        <FiTrash2 size="20" color="#a808b3"/>
                    </button>
                </li>
               ))}
            </ul>
            
        </div>
    );

}
