import React,{useState} from 'react';
import './styles.css';
import { Link } from 'react-router-dom';
import heroesLogo from '../../assets/logo.svg'
import  { FiArrowLeft} from 'react-icons/fi';
import api from '../../services/api';
export default function Newincident(){
    const[title,setTitle] = useState('');
    const[description,setDescription] = useState('');
    const[value,setValue] = useState('');
 async   function handleNewincident(e){
        e.preventDefault();

        const data = {
            title,
            description,
            value,
        };
        try {
            await api.post('incidents',data,{
                headers:{
                    Autho
                }
            })
        } catch (err) {
            alert('Erro ao cadastrar um novo caso!');
        }
    }
    return(
        <div className="new-incident-container">
            <div className="content">
                <section>
                    <img src={heroesLogo} alt="Be The Hero"/>
                    <h1>Cadastra novo caso</h1>
                    <p>Descreva o caso detalhadamente para encontrar um her√≥i para resolver isso</p>
                    <Link className="back-link" to="/profile">
                    <FiArrowLeft size={16}  color="#e02041" />
                   Voltar para home
                </Link>
                </section>
                <form onSubmit={handleNewincident}>
                    <input 
                        placeholder="Titulo do caso"
                        value={title}
                        onChange={e =>setTitle(e.target.value)} 
                    />

                    <textarea placeholder="Descri√ß√£o"
                        value={description}
                        onChange={e =>setDescription(e.target.value)}            
                    />  

                        <input 
                            placeholder="valor em reais"
                            value={value}
                            onChange={e =>setValue(e.target.value)} 
                        />
                            
                    <button className="button" type="submit">Cadastrar</button>
                </form>
            </div>
        </div>
    );
}
